import androidx.compose.ui.Modifier
import androidx.compose.ui.unit.dp
import com.example.attendance_tracker.viewmodel.TimetableViewModel
import com.example.attendance_tracker.data.TimetableEntry
import com.example.attendance_tracker.data.SubjectRepository
import com.example.attendance_tracker.viewmodel.CommonSlotViewModel
import java.time.DayOfWeek
import java.time.format.TextStyle
import java.time.format.DateTimeFormatter
import com.vanpra.composematerialdialogs.MaterialDialog
import com.vanpra.composematerialdialogs.datetime.time.timepicker
import com.vanpra.composematerialdialogs.rememberMaterialDialogState
import java.time.LocalTime
import java.util.Locale
import kotlinx.coroutines.launch
import androidx.compose.foundation.background
import androidx.compose.ui.draw.clip
import androidx.compose.ui.text.style.TextAlign
import androidx.compose.foundation.lazy.LazyColumn
import androidx.compose.foundation.lazy.items

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun TimetableScreen(
    timetableViewModel: TimetableViewModel,
    subjectRepository: SubjectRepository,
    commonSlotViewModel: CommonSlotViewModel = androidx.lifecycle.viewmodel.compose.viewModel()
) {
    val daysOfWeek = DayOfWeek.values().map { it.getDisplayName(TextStyle.FULL, Locale.US) }
    var selectedDay by remember { mutableStateOf(daysOfWeek[0]) }
    val timetableEntries by timetableViewModel.getEntriesForDay(selectedDay).collectAsState()
    var showDialog by remember { mutableStateOf(false) }
    var editingEntry by remember { mutableStateOf<TimetableEntry?>(null) }
    var subject by remember { mutableStateOf("") } 
    val subjects by subjectRepository.allSubjects.collectAsState(initial = emptyList())
    var startTime by remember { mutableStateOf(LocalTime.parse("09:00 AM", DateTimeFormatter.ofPattern("hh:mm a", Locale.US))) }
    var endTime by remember { mutableStateOf(LocalTime.parse("10:00 AM", DateTimeFormatter.ofPattern("hh:mm a", Locale.US))) }
    val snackbarHostState = remember { SnackbarHostState() }
    var errorMessage by remember { mutableStateOf<String?>(null) }
    val coroutineScope = rememberCoroutineScope()
    val slotEntities by commonSlotViewModel.slots.collectAsState() 

    LaunchedEffect(errorMessage) {
        errorMessage?.let {
            snackbarHostState.showSnackbar(it)
            errorMessage = null
        }
    }

    Scaffold(
        topBar = {
            TopAppBar(
                title = { Text("Timetable Editor") },
                actions = {
                    IconButton(onClick = {
                        editingEntry = null
                        subject = ""
                        startTime = LocalTime.parse("09:00 AM", DateTimeFormatter.ofPattern("hh:mm a", Locale.US))
                        endTime = LocalTime.parse("10:00 AM", DateTimeFormatter.ofPattern("hh:mm a", Locale.US))
                        showDialog = true
                    }) {
                        Icon(Icons.Filled.Add, contentDescription = "Add Timetable Entry")
                    }
                }
            )
        },
        snackbarHost = { SnackbarHost(hostState = snackbarHostState) }
    ) { innerPadding ->
        Column(modifier = Modifier.fillMaxSize().padding(innerPadding)) {
            Row(verticalAlignment = Alignment.CenterVertically, modifier = Modifier.padding(8.dp)) {
                Text("Select Day :", modifier = Modifier.padding(end = 8.dp))
                DropdownMenuBox(daysOfWeek, selectedDay) { selectedDay = it }
            }
            Spacer(modifier = Modifier.height(8.dp))
            if (timetableEntries.isEmpty()) {
                Box(modifier = Modifier.fillMaxSize(), contentAlignment = Alignment.Center) {
                    Text("No timetable entries for $selectedDay.")
                }
            } else {
                Column(modifier = Modifier.fillMaxSize()) {
                    timetableEntries.forEach { entry ->
                        Card(
                            modifier = Modifier.fillMaxWidth().padding(8.dp),
                            elevation = CardDefaults.cardElevation(2.dp)
                        ) {
                            Row(modifier = Modifier.padding(16.dp), verticalAlignment = Alignment.CenterVertically) {
                                Column(modifier = Modifier.weight(1f)) {
                                    Text(entry.subject, style = MaterialTheme.typography.titleMedium)
                                    Text("${entry.startTime} - ${entry.endTime}", style = MaterialTheme.typography.bodyMedium)
                                }
                                IconButton(onClick = {
                                    editingEntry = entry
                                    subject = entry.subject
                                    // Enhanced time parsing with fallback and added debugging logs
                                    startTime = try {
                                        val parsedTime = LocalTime.parse(entry.startTime, DateTimeFormatter.ofPattern("hh:mm a", Locale.US))
                                        println("Parsed start time: $parsedTime") // Debugging log
                                        parsedTime
                                    } catch (e: Exception) {
                                        println("Failed to parse start time: '${entry.startTime}' - ${e.message}") // Debugging log
                                        LocalTime.of(9, 0) // Default to 09:00 AM
                                    }
                                    endTime = try {
                                        val parsedTime = LocalTime.parse(entry.endTime, DateTimeFormatter.ofPattern("hh:mm a", Locale.US))
                                        println("Parsed end time: $parsedTime") // Debugging log
                                        parsedTime
                                    } catch (e: Exception) {
                                        println("Failed to parse end time: '${entry.endTime}' - ${e.message}") // Debugging log
                                        LocalTime.of(10, 0) // Default to 10:00 AM
                                    }
                                    showDialog = true
                                }) {
                                    Icon(Icons.Filled.Edit, contentDescription = "Edit")
                                }
                                IconButton(onClick = {
                                    coroutineScope.launch {
                                        timetableViewModel.deleteEntry(entry)
                                        errorMessage = "Entry deleted"
                                    }
                                }) {
                                    Icon(Icons.Filled.Delete, contentDescription = "Delete")
                                }
                            }
                        }
                    }
                }
            }

            if (showDialog) {
                var subjectDropdownExpanded by remember { mutableStateOf(false) }
                val subjectOptions = subjects.map { it.name }
                var selectedSubject by remember { mutableStateOf(subjectOptions.firstOrNull() ?: "") }
                val selectedSlotIds = remember { mutableStateListOf<Int>() }
                var slotSelectionError by remember { mutableStateOf<String?>(null) }
                var dialogErrorMessage by remember { mutableStateOf<String?>(null) }

                AlertDialog(
                    onDismissRequest = { showDialog = false },
                    confirmButton = {
                        Button(onClick = {
                            if (selectedSubject.isBlank() || selectedSlotIds.isEmpty() || slotSelectionError != null) {
                                dialogErrorMessage = slotSelectionError ?: "Please select subject and valid slots."
                                return@Button
                            }
                            // Build selectedSlots from slotEntities and selectedSlotIds
                            val selectedSlots = slotEntities.filter { selectedSlotIds.contains(it.id) }
                                .sortedBy { LocalTime.parse(it.startTime, DateTimeFormatter.ofPattern("hh:mm a", Locale.US)) }
                            val newStart = LocalTime.parse(selectedSlots.first().startTime, DateTimeFormatter.ofPattern("hh:mm a", Locale.US))
                            val newEnd = LocalTime.parse(selectedSlots.last().endTime, DateTimeFormatter.ofPattern("hh:mm a", Locale.US))
                            val conflict = timetableEntries.any { entry ->
                                if (editingEntry != null && entry.id == editingEntry!!.id) return@any false // skip self when editing
                                val entryStart = LocalTime.parse(entry.startTime, DateTimeFormatter.ofPattern("hh:mm a", Locale.US))
                                val entryEnd = LocalTime.parse(entry.endTime, DateTimeFormatter.ofPattern("hh:mm a", Locale.US))
                                // Overlapping time check
                                val timeOverlap = newStart < entryEnd && newEnd > entryStart
                                // Duplicate slot check
                                val slotOverlap = entry.slotIds.any { selectedSlotIds.contains(it) }
                                timeOverlap || slotOverlap
                            }
                            if (conflict) {
                                dialogErrorMessage = "Conflict: Overlapping time or duplicate slot with another entry."
                                return@Button
                            }
                            coroutineScope.launch {
                                val selectedSlots = slotEntities.filter { selectedSlotIds.contains(it.id) }
                                    .sortedBy { LocalTime.parse(it.startTime, DateTimeFormatter.ofPattern("hh:mm a", Locale.US)) }
                                val startStr = selectedSlots.first().startTime
                                val endStr = selectedSlots.last().endTime
                                if (editingEntry == null) {
                                    timetableViewModel.addEntry(
                                        TimetableEntry(
                                            dayOfWeek = selectedDay,
                                            subject = selectedSubject,
                                            startTime = startStr,
                                            endTime = endStr,
                                            slotIds = selectedSlotIds.toList()
                                        )
                                    )
                                    errorMessage = "Entry added"
                                } else {
                                    timetableViewModel.deleteEntry(editingEntry!!)
                                    timetableViewModel.addEntry(
                                        TimetableEntry(
                                            id = editingEntry!!.id,
                                            dayOfWeek = selectedDay,
                                            subject = selectedSubject,
                                            startTime = startStr,
                                            endTime = endStr,
                                            slotIds = selectedSlotIds.toList()
                                        )
                                    )
                                    errorMessage = "Entry updated"
                                }
                                showDialog = false
                            }
                        }) {
                            Text("Save")
                        }
                    },
                    dismissButton = {
                        OutlinedButton(onClick = { showDialog = false }) {
                            Text("Cancel")
                        }
                    },
                    title = { Text(if (editingEntry == null) "Add Timetable Entry" else "Edit Timetable Entry") },
                    text = {
                        Column {
                            Text("Select Subject:")
                            ExposedDropdownMenuBox(
                                expanded = subjectDropdownExpanded,
                                onExpandedChange = { subjectDropdownExpanded = it }
                            ) {
                                OutlinedTextField(
                                    value = selectedSubject,
                                    onValueChange = {},
                                    readOnly = true,
                                    label = { Text("Subject") },
                                    trailingIcon = { ExposedDropdownMenuDefaults.TrailingIcon(expanded = subjectDropdownExpanded) },
                                    modifier = Modifier.menuAnchor()
                                )
                                ExposedDropdownMenu(
                                    expanded = subjectDropdownExpanded,
                                    onDismissRequest = { subjectDropdownExpanded = false }
                                ) {
                                    subjectOptions.forEach { subj ->
                                        DropdownMenuItem(
                                            text = { Text(subj) },
                                            onClick = {
                                                selectedSubject = subj
                                                subjectDropdownExpanded = false
                                            }
                                        )
                                    }
                                }
                            }
                            Spacer(modifier = Modifier.height(16.dp))
                            Text("Select Time Slots (≤20 min gap):")
                            Box(modifier = Modifier.height(220.dp)) {
                                LazyColumn {
                                    items(slotEntities.sortedBy { LocalTime.parse(it.startTime, DateTimeFormatter.ofPattern("hh:mm a", Locale.US)) }) { slot ->
                                        val checked = selectedSlotIds.contains(slot.id)
                                        Button(
                                            onClick = {
                                                if (!checked) {
                                                    val allSelected = selectedSlotIds + slot.id
                                                    val sortedSlots = slotEntities.filter { allSelected.contains(it.id) }
                                                        .sortedBy { LocalTime.parse(it.startTime, DateTimeFormatter.ofPattern("hh:mm a", Locale.US)) }
                                                    val valid = sortedSlots.zipWithNext().all { (a, b) ->
                                                        val aEnd = LocalTime.parse(a.endTime, DateTimeFormatter.ofPattern("hh:mm a", Locale.US))
                                                        val bStart = LocalTime.parse(b.startTime, DateTimeFormatter.ofPattern("hh:mm a", Locale.US))
                                                        java.time.Duration.between(aEnd, bStart).toMinutes() <= 20
                                                    }
                                                    if (valid) {
                                                        selectedSlotIds.add(slot.id)
                                                        slotSelectionError = null
                                                    } else {
                                                        slotSelectionError = "Selected slots must be consecutive with ≤20 min gap."
                                                    }
                                                } else {
                                                    selectedSlotIds.remove(slot.id)
                                                    slotSelectionError = null
                                                }
                                            },
                                            colors = ButtonDefaults.buttonColors(
                                                containerColor = if (checked) MaterialTheme.colorScheme.primary else MaterialTheme.colorScheme.surface,
                                                contentColor = if (checked) MaterialTheme.colorScheme.onPrimary else MaterialTheme.colorScheme.onSurface
                                            ),
                                            modifier = Modifier
                                                .fillMaxWidth()
                                                .padding(vertical = 4.dp)
                                        ) {
                                            Text("${slot.label}: ${slot.startTime} - ${slot.endTime}")
                                        }
                                    }
                                }
                            }
                            if (slotSelectionError != null) {
                                Text(slotSelectionError!!, color = MaterialTheme.colorScheme.error)
                            }
                            if (!dialogErrorMessage.isNullOrBlank()) {
                                Row(
                                    modifier = Modifier
                                        .fillMaxWidth()
                                        .padding(vertical = 8.dp)
                                        .background(
                                            color = MaterialTheme.colorScheme.error.copy(alpha = 0.1f),
                                            shape = MaterialTheme.shapes.medium
                                        )
                                        .padding(12.dp),
                                    verticalAlignment = Alignment.CenterVertically
                                ) {
                                    Icon(
                                        imageVector = Icons.Default.Error,
                                        contentDescription = "Error",
                                        tint = MaterialTheme.colorScheme.error,
                                        modifier = Modifier.size(24.dp)
                                    )
                                    Spacer(modifier = Modifier.width(8.dp))
                                    Text(
                                        text = dialogErrorMessage!!,
                                        color = MaterialTheme.colorScheme.error,
                                        style = MaterialTheme.typography.bodyMedium,
                                        modifier = Modifier.weight(1f)
                                    )
                                }
                            }
                        }
                    }
                )
            }
        }
    }
}

@Composable
fun TimePickerDialogSample(
    selectedTime: LocalTime,
    onTimeSelected: (LocalTime) -> Unit
) {
    val timeDialogState = rememberMaterialDialogState()
    val time = remember { mutableStateOf(selectedTime) }

    Box(
        modifier = Modifier
            .fillMaxWidth()
            .height(48.dp)
            .padding(vertical = 4.dp)
            .clip(MaterialTheme.shapes.medium)
            .background(MaterialTheme.colorScheme.surfaceVariant)
            .clickable { timeDialogState.show() },
        contentAlignment = Alignment.Center
    ) {
        Text(
            text = time.value.format(java.time.format.DateTimeFormatter.ofPattern("hh:mm a", Locale.US)),
            textAlign = TextAlign.Center,
            style = MaterialTheme.typography.bodyLarge,
            color = MaterialTheme.colorScheme.onSurfaceVariant
        )
    }
    MaterialDialog(
        dialogState = timeDialogState,
        buttons = {
            positiveButton("OK")
            negativeButton("Cancel")
        }
    ) {
        timepicker(
            initialTime = time.value,
            title = "Select Time"
        ) { newTime ->
            onTimeSelected(newTime)
            time.value = newTime
        }
    }
}


@Composable
fun DropdownMenuBox(options: List<String>, selected: String, onSelected: (String) -> Unit) {
    var expanded by remember { mutableStateOf(false) }
    Box {
        Button(onClick = { expanded = true }) {
            Text(selected)
        }
        DropdownMenu(expanded = expanded, onDismissRequest = { expanded = false }) {
            options.forEach { option ->
                DropdownMenuItem(text = { Text(option) }, onClick = {
                    onSelected(option)
                    expanded = false
                })
            }
        }
    }
}

package com.example.attendance_tracker.viewmodel

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.example.attendance_tracker.data.Attendance
import com.example.attendance_tracker.data.AttendanceStatus
import com.example.attendance_tracker.repository.AttendanceRepository
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.launch

class AttendanceViewModel(val repository: AttendanceRepository) : ViewModel() {
    private val _attendanceHistory = MutableStateFlow<List<Attendance>>(emptyList())
    val attendanceHistory: StateFlow<List<Attendance>> = _attendanceHistory

    private val _attendancePercentage = MutableStateFlow(0.0)
    val attendancePercentage: StateFlow<Double> = _attendancePercentage

    fun loadAttendance(subjectId: Int) {
        viewModelScope.launch {
            _attendanceHistory.value = repository.getAttendanceHistory(subjectId)
            _attendancePercentage.value = repository.getAttendancePercentage(subjectId)
        }
    }

    fun markAttendance(subjectId: Int, selectedSlots: List<Int>, date: String, status: AttendanceStatus) {
        viewModelScope.launch {
            for (slotId in selectedSlots) {
                repository.markAttendanceForSlot(subjectId, slotId, date, status)
                // Update attended/total classes in Subject
                val subject = repository.getSubjectById(subjectId)
                if (subject != null) {
                    val attended = subject.attendedClasses + if (status == AttendanceStatus.PRESENT) 1 else 0
                    val total = subject.totalClasses + 1
                    repository.updateSubjectAttendance(subjectId, attended, total)
                }
            }
            loadAttendance(subjectId)
        }
    }

    fun updateManualAttendance(subjectId: Int, attended: Int, total: Int, note: String? = null, date: String? = null) {
        viewModelScope.launch {
            repository.updateSubjectAttendance(subjectId, attended, total)
            if (note != null && date != null) {
                repository.addManualHistory(subjectId, date, note)
                // Remove all previous attendance records for this subject
                repository.deleteAllAttendanceForSubject(subjectId)
                // Add new attendance records based on manual values
                val presentCount = attended
                val absentCount = total - attended
                val today = date
                repeat(presentCount) {
                    repository.markAttendanceForSlot(subjectId, -1, today, AttendanceStatus.PRESENT)
                }
                repeat(absentCount) {
                    repository.markAttendanceForSlot(subjectId, -1, today, AttendanceStatus.ABSENT)
                }
            }
            loadAttendance(subjectId)
        }
    }

    suspend fun getAttendanceStatusForSubjectOnDate(subjectId: Int, date: String): AttendanceStatus? {
        val attendance = repository.getAttendanceForDate(subjectId, date)
        return attendance?.status
    }
}

package com.example.attendance_tracker.viewmodel

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.example.attendance_tracker.data.Attendance
import com.example.attendance_tracker.data.AttendanceStatus
import com.example.attendance_tracker.repository.AttendanceRepository
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.launch

class AttendanceViewModel(val repository: AttendanceRepository) : ViewModel() {
    private val _attendanceHistory = MutableStateFlow<List<Attendance>>(emptyList())
    val attendanceHistory: StateFlow<List<Attendance>> = _attendanceHistory

    private val _attendancePercentage = MutableStateFlow(0.0)
    val attendancePercentage: StateFlow<Double> = _attendancePercentage

    fun loadAttendance(subjectId: Int) {
        viewModelScope.launch {
            _attendanceHistory.value = repository.getAttendanceHistory(subjectId)
            _attendancePercentage.value = repository.getAttendancePercentage(subjectId)
        }
    }

    fun markAttendance(subjectId: Int, selectedSlots: List<Int>, date: String, status: AttendanceStatus) {
        viewModelScope.launch {
            // Mark attendance for all slots, but only update attended/total once per subject per day
            selectedSlots.forEach { slotId ->
                repository.markAttendanceForSlot(subjectId, slotId, date, status)
            }
            val subject = repository.getSubjectById(subjectId)
            if (subject != null) {
                val slotCount = selectedSlots.size
                val attended = subject.attendedClasses + if (status == AttendanceStatus.PRESENT) slotCount else 0
                val total = subject.totalClasses + slotCount
                repository.updateSubjectAttendance(subjectId, attended, total)
            }
            loadAttendance(subjectId)
        }
    }

    fun updateManualAttendance(subjectId: Int, attended: Int, total: Int, note: String? = null, date: String? = null) {
        viewModelScope.launch {
            repository.updateSubjectAttendance(subjectId, attended, total)
            if (note != null && date != null) {
                repository.addManualHistory(subjectId, date, note)
                // Remove all previous attendance records for this subject
                repository.deleteAllAttendanceForSubject(subjectId)
                // Add new attendance records based on manual values
                val presentCount = attended
                val absentCount = total - attended
                val today = date
                repeat(presentCount) {
                    repository.markAttendanceForSlot(subjectId, -1, today, AttendanceStatus.PRESENT)
                }
                repeat(absentCount) {
                    repository.markAttendanceForSlot(subjectId, -1, today, AttendanceStatus.ABSENT)
                }
            }
            loadAttendance(subjectId)
        }
    }

    suspend fun getAttendanceStatusForSubjectOnDate(subjectId: Int, date: String): AttendanceStatus? {
        val attendance = repository.getAttendanceForDate(subjectId, date)
        return attendance?.status
    }

    suspend fun updateAttendanceStatusForSubjectOnDate(subjectId: Int, date: String, status: AttendanceStatus) {
        repository.updateAttendanceStatusForSubjectOnDate(subjectId, date, status)
    }

    suspend fun deleteAttendanceForSubjectOnDate(subjectId: Int, date: String) {
        repository.deleteAttendanceForSubjectOnDate(subjectId, date)
    }
}

package com.example.lol

import android.os.Bundle
import androidx.activity.ComponentActivity
import androidx.activity.compose.setContent
import androidx.activity.enableEdgeToEdge
import androidx.compose.foundation.layout.fillMaxSize
import androidx.compose.foundation.layout.padding
import androidx.compose.material3.NavigationBar
import androidx.compose.material3.NavigationBarItem
import androidx.compose.material3.Icon
import androidx.compose.material3.Text
import androidx.compose.material3.Scaffold
import androidx.compose.runtime.Composable
import androidx.compose.ui.Modifier
import androidx.compose.ui.tooling.preview.Preview
import com.example.lol.ui.navigation.AppNavHost
import com.example.lol.ui.navigation.BottomNavItem
import com.example.lol.ui.theme.LOLTheme
import androidx.navigation.compose.rememberNavController
import androidx.compose.material.icons.Icons
import androidx.compose.material.icons.filled.School
import androidx.compose.material.icons.filled.List
import androidx.compose.material.icons.filled.Home
import androidx.compose.material3.ExperimentalMaterial3Api
import androidx.room.Room
import com.example.lol.data.AppDatabase
import com.example.lol.data.SubjectRepository
import com.example.lol.repository.TimetableRepository
import com.example.lol.viewmodel.TimetableViewModel
import com.example.lol.viewmodel.TimetableViewModelFactory
import androidx.compose.runtime.remember

@OptIn(ExperimentalMaterial3Api::class)
class MainActivity : ComponentActivity() {
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        enableEdgeToEdge()
        setContent {
            LOLTheme {
                val navController = rememberNavController()
                val context = this
                val db = remember {
                    Room.databaseBuilder(
                        context.applicationContext,
                        AppDatabase::class.java,
                        "attendance_db"
                    )
                    .fallbackToDestructiveMigration()
                    .build()
                }
                val repository = remember { SubjectRepository(db.subjectDao()) }
                val timetableRepository = remember { TimetableRepository(db.timetableDao()) }
                val timetableViewModel: TimetableViewModel = androidx.lifecycle.viewmodel.compose.viewModel(
                    factory = TimetableViewModelFactory(timetableRepository)
                )
                val items = listOf(
                    BottomNavItem.Home,
                    BottomNavItem.Subjects,
                    BottomNavItem.Timetable,
                    BottomNavItem.CommonSlots
                )
                val commonSlotViewModel: com.example.lol.viewmodel.CommonSlotViewModel = androidx.lifecycle.viewmodel.compose.viewModel()
                // Always call insertDefaultSlotsIfEmpty; it only inserts if table is empty
                commonSlotViewModel.insertDefaultSlotsIfEmpty()
                val attendanceRepository = remember { com.example.lol.repository.AttendanceRepository(db.attendanceDao(), db.subjectDao()) }
                val attendanceViewModel = remember { com.example.lol.viewmodel.AttendanceViewModel(attendanceRepository) }
                Scaffold(
                    modifier = Modifier.fillMaxSize(),
                    bottomBar = {
                        NavigationBar {
                            val currentRoute = navController.currentBackStackEntry?.destination?.route
                            items.forEach { item ->
                                NavigationBarItem(
                                    icon = { Icon(item.icon, contentDescription = item.label) },
                                    label = { Text(item.label) },
                                    selected = currentRoute == item.route,
                                    onClick = {
                                        if (currentRoute != item.route) {
                                            navController.navigate(item.route) {
                                                popUpTo(navController.graph.startDestinationId) { saveState = true }
                                                launchSingleTop = true
                                                restoreState = true
                                            }
                                        }
                                    }
                                )
                            }
                        }
                    }
                ) { innerPadding ->
                    AppNavHost(
                        navController = navController,
                        repository = repository,
                        timetableViewModel = timetableViewModel,
                        commonSlotViewModel = commonSlotViewModel,
                        attendanceViewModel = attendanceViewModel,
                        modifier = Modifier.padding(innerPadding)
                    )
                }
            }
        }
    }
}

package com.example.lol

import android.os.Bundle
import androidx.activity.ComponentActivity
import androidx.activity.compose.setContent
import androidx.activity.enableEdgeToEdge
import androidx.compose.foundation.layout.fillMaxSize
import androidx.compose.foundation.layout.padding
import androidx.compose.material3.NavigationBar
import androidx.compose.material3.NavigationBarItem
import androidx.compose.material3.Icon
import androidx.compose.material3.Text
import androidx.compose.material3.Scaffold
import androidx.compose.runtime.Composable
import androidx.compose.ui.Modifier
import androidx.compose.ui.tooling.preview.Preview
import com.example.lol.ui.navigation.AppNavHost
import com.example.lol.ui.navigation.BottomNavItem
import com.example.lol.ui.theme.LOLTheme
import androidx.navigation.compose.rememberNavController
import androidx.compose.material.icons.Icons
import androidx.compose.material.icons.filled.School
import androidx.compose.material.icons.filled.List
import androidx.compose.material.icons.filled.Home
import androidx.compose.material3.ExperimentalMaterial3Api
import androidx.room.Room
import com.example.lol.data.AppDatabase
import com.example.lol.data.SubjectRepository
import com.example.lol.repository.TimetableRepository
import com.example.lol.viewmodel.TimetableViewModel
import com.example.lol.viewmodel.TimetableViewModelFactory
import androidx.compose.runtime.remember

@OptIn(ExperimentalMaterial3Api::class)
class MainActivity : ComponentActivity() {
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        enableEdgeToEdge()
        setContent {
            LOLTheme {
                val navController = rememberNavController()
                val context = this
                val db = remember {
                    Room.databaseBuilder(
                        context.applicationContext,
                        AppDatabase::class.java,
                        "attendance_db"
                    )
                    .fallbackToDestructiveMigration()
                    .build()
                }
                val repository = remember { SubjectRepository(db.subjectDao()) }
                val timetableRepository = remember { TimetableRepository(db.timetableDao()) }
                val timetableViewModel: TimetableViewModel = androidx.lifecycle.viewmodel.compose.viewModel(
                    factory = TimetableViewModelFactory(timetableRepository)
                )
                val items = listOf(
                    BottomNavItem.Home,
                    BottomNavItem.Subjects,
                    BottomNavItem.Timetable,
                    BottomNavItem.CommonSlots
                )
                val commonSlotViewModel: com.example.lol.viewmodel.CommonSlotViewModel = androidx.lifecycle.viewmodel.compose.viewModel()
                // Always call insertDefaultSlotsIfEmpty; it only inserts if table is empty
                commonSlotViewModel.insertDefaultSlotsIfEmpty()
                val attendanceRepository = remember { com.example.lol.repository.AttendanceRepository(db.attendanceDao(), db.subjectDao(), repository) }
                val attendanceViewModel = remember { com.example.lol.viewmodel.AttendanceViewModel(attendanceRepository) }
                Scaffold(
                    modifier = Modifier.fillMaxSize(),
                    bottomBar = {
                        NavigationBar {
                            val currentRoute = navController.currentBackStackEntry?.destination?.route
                            items.forEach { item ->
                                NavigationBarItem(
                                    icon = { Icon(item.icon, contentDescription = item.label) },
                                    label = { Text(item.label) },
                                    selected = currentRoute == item.route,
                                    onClick = {
                                        if (currentRoute != item.route) {
                                            navController.navigate(item.route) {
                                                popUpTo(navController.graph.startDestinationId) { saveState = true }
                                                launchSingleTop = true
                                                restoreState = true
                                            }
                                        }
                                    }
                                )
                            }
                        }
                    }
                ) { innerPadding ->
                    AppNavHost(
                        navController = navController,
                        repository = repository,
                        timetableViewModel = timetableViewModel,
                        commonSlotViewModel = commonSlotViewModel,
                        attendanceViewModel = attendanceViewModel,
                        modifier = Modifier.padding(innerPadding)
                    )
                }
            }
        }
    }
}

package com.example.lol.repository
import com.example.lol.data.Attendance
import com.example.lol.data.AttendanceDao
import com.example.lol.data.AttendanceStatus
import com.example.lol.data.SubjectRepository
import com.example.lol.data.SubjectDao
import com.example.lol.data.Subject

class AttendanceRepository(private val attendanceDao: AttendanceDao, private val subjectDao: SubjectDao) {
    suspend fun markAttendanceForSlot(subjectId: Int, slotId: Int, date: String, status: AttendanceStatus) {
        val attendance = Attendance(subjectId = subjectId, slotId = slotId, date = date, status = status)
        attendanceDao.insertAttendance(attendance)
    }

    suspend fun deleteAllAttendanceForSubject(subjectId: Int) {
        attendanceDao.deleteAllForSubject(subjectId)
    }

    suspend fun getSubjectById(subjectId: Int): Subject? {
        return subjectDao.getSubjectById(subjectId)
    }

    suspend fun addManualHistory(subjectId: Int, date: String, note: String) {
        val manual = Attendance(subjectId = subjectId, slotId = -1, date = date, status = AttendanceStatus.PRESENT, note = note)
        attendanceDao.insertManualHistory(manual)
    }

    suspend fun updateSubjectAttendance(subjectId: Int, attended: Int, total: Int) {
        val subject = subjectDao.getSubjectById(subjectId)
        if (subject != null) {
            val updated = subject.copy(attendedClasses = attended, totalClasses = total)
            subjectDao.updateSubject(updated)
            // Notify SubjectRepository to refresh subjects
            SubjectRepository(subjectDao).refreshSubjects()
        }
    }

    suspend fun getAttendanceHistory(subjectId: Int) = attendanceDao.getAttendanceForSubject(subjectId)

    suspend fun getAttendanceForDate(subjectId: Int, date: String) = attendanceDao.getAttendanceForSubjectOnDate(subjectId, date)

    suspend fun getAttendancePercentage(subjectId: Int): Double {
        val total = attendanceDao.getTotalMarked(subjectId)
        if (total == 0) return 0.0
        val present = attendanceDao.getCountByStatus(subjectId, AttendanceStatus.PRESENT)
        return (present.toDouble() / total) * 100
    }
}

package com.example.lol.repository
import com.example.lol.data.Attendance
import com.example.lol.data.AttendanceDao
import com.example.lol.data.AttendanceStatus
import com.example.lol.data.SubjectRepository
import com.example.lol.data.SubjectDao
import com.example.lol.data.Subject

class AttendanceRepository(
    private val attendanceDao: AttendanceDao,
    private val subjectDao: SubjectDao,
    private val subjectRepository: SubjectRepository // Injected shared instance
) {
    suspend fun markAttendanceForSlot(subjectId: Int, slotId: Int, date: String, status: AttendanceStatus) {
        val attendance = Attendance(subjectId = subjectId, slotId = slotId, date = date, status = status)
        attendanceDao.insertAttendance(attendance)
    }

    suspend fun deleteAllAttendanceForSubject(subjectId: Int) {
        attendanceDao.deleteAllForSubject(subjectId)
    }

    suspend fun getSubjectById(subjectId: Int): Subject? {
        return subjectDao.getSubjectById(subjectId)
    }

    suspend fun addManualHistory(subjectId: Int, date: String, note: String) {
        val manual = Attendance(subjectId = subjectId, slotId = -1, date = date, status = AttendanceStatus.PRESENT, note = note)
        attendanceDao.insertManualHistory(manual)
    }

    suspend fun updateSubjectAttendance(subjectId: Int, attended: Int, total: Int) {
        val subject = subjectDao.getSubjectById(subjectId)
        if (subject != null) {
            val updated = subject.copy(attendedClasses = attended, totalClasses = total)
            subjectDao.updateSubject(updated)
            // Notify shared SubjectRepository to refresh subjects
            subjectRepository.refreshSubjects()
        }
    }

    suspend fun getAttendanceHistory(subjectId: Int) = attendanceDao.getAttendanceForSubject(subjectId)

    suspend fun getAttendanceForDate(subjectId: Int, date: String) = attendanceDao.getAttendanceForSubjectOnDate(subjectId, date)

    suspend fun getAttendancePercentage(subjectId: Int): Double {
        val total = attendanceDao.getTotalMarked(subjectId)
        if (total == 0) return 0.0
        val present = attendanceDao.getCountByStatus(subjectId, AttendanceStatus.PRESENT)
        return (present.toDouble() / total) * 100
    }
}
